def countmethod(handle)
  count = 0
  handle.each do |user|
    number = 0
    user.split("").each do |check|
      if check >= "0" and check <= "9"
        number = 1
      end
    end
    if number == 1
      count += 1
      # puts user
    end
  end
  return count
end



puts "Il y a #{countmethod(reporter)} journalistes sur Twitter avec un numero dans leurs username dans le tableau 'reporter"


def aude(handle)
  count = 0
  handle.each do |user|
  	  
  	number = 0

  	if (user.downcase).include?('aude')
  		number =1
  	end

  	if number == 1
  		count +=1
  		# puts user
  	end
  end
  return count
end

puts "Il y a #{aude(reporter)} journaliste twitter contenant les lettres 'aude' a la suite dans le tableau 'reporter'"


def capitalized(handle)
  count = 0

  handle.each do |user|

  	number = 0
  	

  	if user[1] == user[1].upcase 
  		number = 1
  		count +=1
  	 end
  	 
  end
  return count
end

puts "Il y a #{capitalized(reporter)} journaliste twitter dont les username commencent par une majuscule dans le tableau 'reporter' "


def countmaj(handle)
  count = 0
  handle.each do |user|
    number = 0
      first = 0
    user.split("").each do |check|

    
    	if first != 0

      if check == check.upcase
        number = 1
      end
    	end
    	first += 1
    end
    	if number == 1
      	count += 1
      	# puts user
    	end

  end
  return count
end

puts "Il y a #{countmaj(reporter)} journalistes sur Twitter avec une maj dans l'username dans le tableau 'reporter'"


def underline(handle)
  underline = 0
  handle.each do |user|
    if user.include?("_")
      underline +=1
    end
  end 
  return underline
end

puts "Il y a #{underline(reporter)} journalistes twitter avec un '_' dans leurs username dans le tableau 'reporter'"



